        private void DoReadBestGoods()
        {
            {
                m_GoodsTable = App.Instance().DBManager.ReadGoods(5, "");
                int _idx = 0;
                foreach (DataRow _db_row in m_GoodsTable.Rows)
                {
                    m_goodsButtons[_idx].GoodsUcode = Convert.ToInt32(_db_row["goods_number"]);
                    string _tempo = Convert.ToString(_db_row["goods_picture"]);
                    if (_tempo.Length <= 0)
                    {
                        _tempo = "";
                    }
                    byte[] _byte = HexStringToByteArray(_tempo);
                    m_goodsButtons[_idx].GoodsImage = new Bitmap(new MemoryStream(_byte));                     
                    m_goodsButtons[_idx].GoodsName = Convert.ToString(_db_row["goods_name"]);
                    m_goodsButtons[_idx].GoodsPrice = Convert.ToInt32(_db_row["goods_price"]);

                    m_goodsButtons[_idx].Visible = true;
                    _idx++;
                    if (_idx > 8) { break; }
                }
                if (_idx < 8)
                {
                    for (int hdx = _idx; hdx < 8; hdx++)
                    {
                        m_goodsButtons[hdx].Visible = false;
                    }
                }
            }
        }
