        //장바구니 물품목록 클릭
        private int GoodsButtonClick(object sender, object param)
        {
            GoodsButton _button = (GoodsButton)sender;
            DataTable _dp_orderdetail = DisplaySet.Tables["dp_orderdetail"];
            DataRow[] _od_rows = _dp_orderdetail.Select(string.Format("goods_number = {0}", _button.GoodsUcode));
            if (_od_rows != null && _od_rows.Length > 0)
            {
                int _quantity = Convert.ToInt32(_od_rows[0]["od_quantity"]);
                _od_rows[0]["od_quantity"] = _quantity + 1;
            }
            else
            {
                DataRow[] _goods_rows = m_GoodsTable.Select(string.Format("goods_number = {0}", _button.GoodsUcode));
                if (_goods_rows != null && _goods_rows.Length > 0)
                {
                    DataRow _dp_row = _dp_orderdetail.NewRow();
                    _dp_row["od_quantity"] = 1;
                    _dp_row["od_price"] = _goods_rows[0]["goods_price"];
                    _dp_row["goods_number"] = _goods_rows[0]["goods_number"];
                    _dp_row["goods_name"] = _goods_rows[0]["goods_name"];
                    _dp_row["goods_description"] = _goods_rows[0]["goods_description"];
                    _dp_orderdetail.Rows.Add(_dp_row);

                    p_description.Text = string.Format("{0}", _dp_row["goods_description"]);
                    p_label.Text = string.Format("{0}", _dp_row["goods_name"]);

                }
            }
            DisplayTotalAmount();
            return 0;
        }

        private int m_total_sum = 0;
        //displayset 정리
        private void DisplayTotalAmount()
        {
            DataTable _dp_orderdetail = DisplaySet.Tables["dp_orderdetail"];
            m_total_sum = 0;
            foreach (DataRow _row in _dp_orderdetail.Rows)
            {
                int _quantity = Convert.ToInt32(_row["od_quantity"]);
                int _price = Convert.ToInt32(_row["od_price"]);
                int _unit_sum = _quantity * _price;
                m_total_sum += _unit_sum;
            }
            all_value.Text = string.Format("{0:N0} 원", m_total_sum);

        }

        //제품 빼기 더하기
        private void btn_sub_product_Click(object sender, EventArgs e)
        {

            DataRow row = GridManager.SelectedRow(Market_detail);
            if (row != null)
            {
                int _od_quantity = Convert.ToInt32(row["od_quantity"]) - 1;
                if (_od_quantity > 0)
                {
                    row["od_quantity"] = _od_quantity;
                }
                else
                {
                    DisplaySet.Tables["dp_orderdetail"].Rows.Remove(row);
                }
            }
            else
            {
            }
            DisplayTotalAmount();
        }
        private void btn_add_product_Click(object sender, EventArgs e)
        {
            DataRow row = GridManager.SelectedRow(Market_detail);
            if (row != null)
            {
                row["od_quantity"] = Convert.ToInt32(row["od_quantity"]) + 1;
            }
            else
            {
            }
            DisplayTotalAmount();          
        }

        //결제창
        private void btn_pay_shop_Click(object sender, EventArgs e)
        {
            Kiosk_Card_Pop _Pop = new Kiosk_Card_Pop();
            DialogResult _result = _Pop.ShowDialog(ePopMode.none, null);
            if (_result == DialogResult.OK)
            {
                //DB저장
                int _waiting_num = DoSaveOrder("SHOP");

                kiosk_Watingnumber_Pop _Watingnumber_Pop = new kiosk_Watingnumber_Pop();
                _Watingnumber_Pop.ShowDialog(ePopMode.none, _waiting_num);
            }
        }
        private void btn_pay_wrap_Click(object sender, EventArgs e)
        {
            Kiosk_Card_Pop _Pop = new Kiosk_Card_Pop();
            DialogResult _result = _Pop.ShowDialog(ePopMode.none, null);
            if (_result == DialogResult.OK)
            {
                int _wating_num = DoSaveOrder("Packing");

                kiosk_Watingnumber_Pop _Watingnumber_pop = new kiosk_Watingnumber_Pop();
                _Watingnumber_pop.ShowDialog(ePopMode.none, _wating_num);
            }
        }
        private int DoSaveOrder(string _ol_paymenttype)
        {
            //대기번호구하기
            int _ol_watingnumber = App.Instance().DBManager.ReadSeq("seq_waitnum");
            int _ol_number = App.Instance().DBManager.ReadSeq("seq_order");
            //주문리스트추가
            string _ol_date = System.DateTime.Now.ToString("yyyy-MM-dd HH:mm:ss");
            int _result = App.Instance().DBManager.AddOrderList(_ol_number, _ol_watingnumber, m_total_sum, _ol_paymenttype, 0);
            //주문상세 추가
            DataTable _dp_orderdetail = DisplaySet.Tables["dp_orderdetail"];
            foreach (DataRow _row in _dp_orderdetail.Rows)
            {
                int _od_quantity = Convert.ToInt32(_row["od_quantity"]);
                int _od_price = Convert.ToInt32(_row["od_price"]);
                int _goods_number = Convert.ToInt32(_row["goods_number"]);
                string _goods_name = Convert.ToString(_row["goods_name"]);

                App.Instance().DBManager.AddOrderDetail(_od_quantity, _od_price, _ol_number, _goods_number, _goods_name);
            }
            return _ol_watingnumber;
        }

